import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ProductoService } from 'src/app/services/producto.service';
import { LocalService } from 'src/app/services/local.service';
import { DetalleCompra } from 'src/app/interfaces/detalle-compra';
import { CompraService } from 'src/app/services/compra.service';

@Component({
  selector: 'app-comprar-producto',
  templateUrl: './comprar-producto.component.html',
  styleUrls: ['./comprar-producto.component.css']
})
export class ComprarProductoComponent implements OnInit {
  Id!: number;
  localNombre: string = '';
  cantidadSeleccionada: number = 1;
  precioUnitario: number = 0;
  subtotal: number = 0;
  localId: number = -1; // Inicializar localId con un valor predeterminado

  constructor(private aRoute: ActivatedRoute, private productoService: ProductoService, private localService: LocalService, private router: Router,private compraService: CompraService ) {}

  ngOnInit() {
    this.Id = Number(this.aRoute.snapshot.paramMap.get('id'));
    this.productoService.getProducto(this.Id).subscribe(producto => {
      if (producto.localId) {
        this.localService.getLocal(producto.localId).subscribe(local => {
          this.localNombre = local.nombreLo;
          this.localId = local.id ?? -1;
        });
      }
      this.precioUnitario = producto.precioVendido;
    });
  }
  seleccionarCantidad(cantidad: number) {
    this.cantidadSeleccionada = cantidad;
    this.calcularSubtotal(); // Recalcula el subtotal cuando se cambia la cantidad
  }
  calcularSubtotal() {
    this.subtotal = this.precioUnitario * this.cantidadSeleccionada;
  }

  agregarAlCarrito() {
    this.calcularSubtotal();

    const detalleCompra: DetalleCompra = {
      productoId: this.Id,
      cantidad: this.cantidadSeleccionada,
      precioUnitario: this.precioUnitario,
      subtotal: this.subtotal,
      localId: this.localId || -1, // Manejar el caso de localId siendo undefined
    };

    this.compraService.agregarAlCarrito([detalleCompra]).subscribe(
      () => {
        console.log('Producto agregado al carrito exitosamente');
        // Redirigir al componente del carro después de agregar el producto
        this.router.navigate(['/carro'], { queryParams: { detalleCompraId: detalleCompra.id } });
      },
      error => {
        console.error('Error al agregar el producto al carrito:', error);
      }
    );
  }
}
