import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Compra } from '../interfaces/compra';
import { HttpClient } from '@angular/common/http';
import { environment } from 'src/enviroments/environment.development';
import { DetalleCompra } from '../interfaces/detalle-compra';

@Injectable({
  providedIn: 'root'
})
export class CompraService {

  private myAppUrl: string = environment.Url;
  private myApiUrl: string = 'api/Compra/';

  constructor(private http: HttpClient) { }

  getProducto(Id:number):Observable<Compra>{
    return this.http.get<Compra>(`${this.myAppUrl}${this.myApiUrl}${Id}`);

  }
   deleteProducto(Id:number):Observable<void>{
    return this.http.delete<void>(`${this.myAppUrl}${this.myApiUrl}${Id}`);

  }
  addProducto(compra:Compra):Observable<Compra>{
    return this.http.post<Compra>(`${this.myAppUrl}${this.myApiUrl}`, compra)

  }


  updateProducto(Id:number, compra:Compra):Observable<void> {
    return this.http.put<void>(`${this.myAppUrl}${this.myApiUrl}${Id}`, compra);
  }
  agregarAlCarrito(detallesCompra: DetalleCompra[]): Observable<void> {
    return this.http.post<void>(`${this.myAppUrl}${this.myApiUrl}agregar-al-carrito`, detallesCompra);
  }
}
