import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { environment } from 'src/enviroments/environment.development';

@Injectable({
  providedIn: 'root'
})
export class CarroService {
  private myAppUrl: string = environment.Url;
  private myApiUrl: string = 'api/Carro/';
  private contadorProductosSubject: BehaviorSubject<number> = new BehaviorSubject<number>(0);
  public contadorProductos$: Observable<number> = this.contadorProductosSubject.asObservable();


  constructor(private http: HttpClient) {}

  agregarProductoAlCarrito(productoId: number): Observable<any> {
    // Lógica para agregar el producto al carrito en tu backend
    return this.http.post(`${this.myApiUrl}/carrito/agregar`, { productoId });
  }

  getProductosCarrito(): Observable<any[]> {
    // Lógica para obtener los productos del carrito desde tu backend
    return this.http.get<any[]>(`${this.myApiUrl}/carrito`);
  }
  actualizarContadorProductos(): void {
    this.getProductosCarrito().subscribe(productos => {
      this.contadorProductosSubject.next(productos.length);
    });
  }
}
