import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Router, ActivatedRoute } from '@angular/router';
import { ProductoService } from 'src/app/services/producto.service';

@Component({
  selector: 'app-add-prodict',
  templateUrl: './add-prodict.component.html',
  styleUrls: ['./add-prodict.component.css']
})
export class AddProdictComponent implements OnInit{
  loading: boolean = false;
  form: FormGroup;
  Id: number;
  operacion: string='Agregar';
  localid: string; // Agrega esta línea

  constructor(
    private fb: FormBuilder,
    private _productoService: ProductoService,
    private router: Router,
    private aRoute: ActivatedRoute,
    private _snackBar: MatSnackBar,
  ) {
    this.form = this.fb.group({
      nombrePro: ['', Validators.required],
      descripcionPro: ['', Validators.required],
      categoriaP: ['', Validators.required],
      imagen: ['', Validators.required],
      precio: ['', Validators.required],
      cantidadPro: ['', Validators.required],
    });

    // Obtén el localId del parámetro de la URL
    this.localid = this.aRoute.snapshot.paramMap.get('Id') ?? '';

    // Convierte el id de la URL a número si es necesario
    this.Id = Number(this.aRoute.snapshot.paramMap.get('Id'));
  }

  ngOnInit(): void {
    if (this.Id != 0) {
      this.operacion = 'Editar';
      this.obtenerPro(this.Id);
    }
  }

  obtenerPro(Id: number) {
    this.loading = true;
    this._productoService.getProducto(Id).subscribe(data => {
      this.form.setValue({
        nombrePro: data.nombrePro,
        descripcionPro: data.descripcionPro,
        direccion: data.categoriaP,
        cantidadPro: data.cantidadPro,
        precio: data.precio,
        imagen: data.imagen,
      });
      this.loading = false;
    });
  }

  agregarEditarProducto() {
    if (this.localid) {
      const producto: Producto = {
        nombrePro: this.form.value.nombrePro,
        descripcionPro: this.form.value.descripcionPro,
        categoriaP: this.form.value.categoriaP,
        cantidadPro: this.form.value.cantidadPro,
        precio: this.form.value.precio,
        imagen: this.form.value.imagen,
        localid: this.localid,
      };

      if (this.Id != 0) {
        producto.Id = this.Id;
        this.editarPro(this.Id, producto);
      } else {
        this.agregarPro(producto);
      }
    } else {
      console.error('ProductoId es nulo o indefinido');
    }
  }

  editarPro(Id: number, producto: Producto) {
    this.loading = true;
    this._productoService.updateProducto(Id, producto).subscribe(() => {
      this.loading = false;
      console.log('llegue')
      this.mensajeExito('actualizado');
      this.router.navigate(['/']);
    })
  }

  agregarPro(producto:Producto) {
    this._productoService.addProducto(producto).subscribe(data => {
      this.mensajeExito('registrado');
      this.router.navigate(['']);
    })
  }

  mensajeExito(texto: string) {
    this._snackBar.open(`El producto fue ${texto} con éxito`,'', {
      duration: 4000,
      horizontalPosition: 'right',
    });
  }

}

export interface Producto {
    Id?:number,
    nombrePro: string;
    categoriaP: string;
    descripcionPro: string;
    localid:string,
    cantidadPro: string;
    precio: string;
    imagen: string;
}
