import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthenticationService } from 'src/app/services/authentication.service';
import { CarroService } from 'src/app/services/carro.service'; // Importa el servicio del carrito

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent implements OnInit {
  isLoggedIn: boolean = false;
  userData: any;
  userName: string = ''; // Definir userName
  contadorProductos: number = 0;

  searchTerm: string = '';

  constructor(
    private router: Router,
    private authService: AuthenticationService,
    private carroService: CarroService // Inyecta el servicio del carrito
  ) {}

  ngOnInit(): void {
    this.authService.isLoggedIn$.subscribe((isLoggedIn) => {
      this.isLoggedIn = isLoggedIn;
      if (isLoggedIn) {
        const userDataString = localStorage.getItem('userData');
        if (userDataString) {
          this.userData = JSON.parse(userDataString);
          this.userName = this.userData.nombre; // Asignar el valor del nombre de usuario
        }
      }
    });

    // Suscríbete al Observable de productos en el carrito para actualizar el número de productos
    this.carroService.contadorProductos$.subscribe(contador => {
      this.contadorProductos = contador;
    });
  }

  search(): void {
    this.router.navigate(['/search'], { queryParams: { term: this.searchTerm } });
  }

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
