import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-comprar-producto',
  templateUrl: './comprar-producto.component.html',
  styleUrls: ['./comprar-producto.component.css']
})
export class ComprarProductoComponent implements OnInit {
  productoId: number;

  constructor(private activatedRoute: ActivatedRoute) {
    // Obtener el Id de la ruta
    this.productoId = Number(this.activatedRoute.snapshot.paramMap.get('productoId'));
  }

  ngOnInit(): void {
    // Puedes realizar operaciones adicionales al cargar el componente, por ejemplo, cargar detalles del producto
    // utilizando el Id obtenido.
  }
}
