import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { environment } from 'src/enviroments/environment.development';
import { Foto } from '../interfaces/foto';
import { map } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class FotoService {
  private myAppUrl: string = environment.Url;
  private myApiUrl: string = 'api/Local/';

  constructor(private http: HttpClient) { }
  getFotosByLocalId(localId: string): Observable<Foto[]> {
    console.log('URL:', `${this.myAppUrl}api/Foto/local/${localId}`); // Ajusta la ruta aquí

    return this.http.get<Foto[]>(`${this.myAppUrl}api/Foto/local/${localId}`); // Ajusta la ruta aquí
  }

  getFotoUrlByProductoId(productoId: string): Observable<string> {
    return this.http.get<Foto[]>(`${this.myAppUrl}api/Foto/producto/${productoId}`).pipe(
      map((fotos: Foto[]) => {
        if (fotos.length > 0) {
          return fotos[0].data; // Suponiendo que la URL de la foto se encuentra en la propiedad 'data'
        } else {
          return ''; // O cualquier valor por defecto en caso de que no haya foto disponible
        }
      })
    );
  }
}
