import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { ProductoService } from 'src/app/services/producto.service';
import { Producto } from '../store/add-prodict/add-prodict.component';

@Component({
  selector: 'app-search-result',
  templateUrl: './search-result.component.html',
  styleUrls: ['./search-result.component.css']
})
export class SearchResultComponent implements OnInit {
  searchForm!: FormGroup;
  productos: Producto[] = [];

  constructor(
    private formBuilder: FormBuilder,
    private activatedRoute: ActivatedRoute,
    private productoService: ProductoService // Reemplaza con la inyección correcta
  ) {}

  ngOnInit(): void {
    ;

    // Obtén la consulta de la URL si la hay
    this.activatedRoute.queryParams.subscribe((params) => {
      const categoria = params['categoria'];
      // Puedes hacer algo con el valor de la categoría, como setearlo en el formulario
    });

    // Llama a la función para obtener productos con los filtros actuales
    this.getFilteredProductos();
  }

  getFilteredProductos(): void {
    // Obtén los valores del formulario de búsqueda
    const filters = this.searchForm.value;

    // Llama al servicio para obtener los productos filtrados
    this.productoService.getProductosConFiltros(filters).subscribe((productos) => {
      this.productos = productos;
    });
  }
}
