// search-result.component.ts
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Producto } from 'src/app/interfaces/producto';
import { ProductoService } from 'src/app/services/producto.service';

@Component({
  selector: 'app-search-result',
  templateUrl: './search-result.component.html',
  styleUrls: ['./search-result.component.css']
})
export class SearchResultComponent implements OnInit {
  searchTerm: string = ''; // Inicializado aquí
  productos: Producto[] = [];

  constructor(
    private activatedRoute: ActivatedRoute,
    private productoService: ProductoService
  ) {}

  ngOnInit(): void {
    this.activatedRoute.queryParams.subscribe((params) => {
      this.searchTerm = params['term'] || '';
      this.getFilteredProductos();
    });
  }

  getFilteredProductos(): void {
    // Obtén los productos según el término de búsqueda
    this.productoService.getProductosBySearchTerm(this.searchTerm).subscribe((productos) => {
      this.productos = productos;
    });
  }
}
