import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Subscription } from 'rxjs';
import { ProductoService } from 'src/app/services/producto.service';
import { Producto } from '../store/add-prodict/add-prodict.component';

@Component({
  selector: 'app-comprar-producto',
  templateUrl: './comprar-producto.component.html',
  styleUrls: ['./comprar-producto.component.css']
})
export class ComprarProductoComponent implements OnInit {
  loading: boolean=false;
  producto!: Producto;
  Id!:number;
  routeSub!: Subscription;

  constructor(private _productoService :ProductoService,
    private aRoute:ActivatedRoute){


  }
  ngOnInit() {
    this.Id = Number(this.aRoute.snapshot.paramMap.get('Id'));
    this.obtenerPro();
  }

  ngOnDestroy(): void {
    /* this.routeSub.unsubscribe() */
  }
  obtenerPro() {
    this.loading = true;
    this._productoService.getProducto(this.Id).subscribe((data) => {
      console.log(data);
      this.producto = data;
      this.loading = false;
    });
  }

}
