import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthenticationService } from 'src/app/services/authentication.service';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent implements OnInit {
  isLoggedIn: boolean = false;
  userData: any;
  userName: string = '';
  contadorProductos: number = 0;
  searchTerm: string = '';
  isDropdownOpen: boolean = false; // Variable para controlar la visibilidad del menú desplegable

  constructor(
    private router: Router,
    private authService: AuthenticationService,
  ) {}

  ngOnInit(): void {
    this.authService.isLoggedIn$.subscribe((isLoggedIn) => {
      this.isLoggedIn = isLoggedIn;
      if (isLoggedIn) {
        const userDataString = localStorage.getItem('userData');
        if (userDataString) {
          this.userData = JSON.parse(userDataString);
          this.userName = this.userData.nombre;
        }
      }
    });
  }

  search(): void {
    this.router.navigate(['/search'], { queryParams: { term: this.searchTerm } });
  }

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/login']);
  }

  toggleDropdown(): void {
    this.isDropdownOpen = !this.isDropdownOpen; // Cambia el estado de visibilidad del menú desplegable
  }
}
