import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-comprar-producto',
  templateUrl: './comprar-producto.component.html',
  styleUrls: ['./comprar-producto.component.css']
})
export class ComprarProductoComponent implements OnInit {
  Id!: number;

  constructor(private aRoute: ActivatedRoute) {}

  ngOnInit() {
    this.Id = Number(this.aRoute.snapshot.paramMap.get('id'));
  }
}


<app-navbar></app-navbar>

<body>
  <div class="sidebar">
    <!-- Botones -->
    <div class="button-container">
      <button mat-raised-button color="primary">Botón 1</button>
      <button mat-raised-button color="primary">Botón 2</button>
    </div>
    <ul class="number-list">
      <li>1</li>
      <li>2</li>
      <li>3</li>
      <li>4</li>
      <li>5</li>
      <li>6</li>
    </ul>
  </div>

  <div class="dash">

    <!-- HEADER -->


    <!-- BODY -->

      <!-- SIDEBAR -->

      <main class="main">
        <app-ver-producto [id]="Id"></app-ver-producto>
      </main>


    </div>


</body>
